import React, { Component } from 'react';

import './App.css';


import Register from './components/Register/Register';
import SignIn from './components/SignIn/SignIn';
import TextBox from './components/TextBox/TextBox';
import ResultBox from './components/TextBox/ResultBox';
import Navigation from './components/Navigation/Navigation';
import Header from './components/Header/Header';
// import Error from './components/Error/Error';




class App extends Component {
	constructor(){
		super()
			this.state= {
				initialstate: true,
				task: '',
				number: '',
        route: 'signin',
        isSignedIn: false,
        user: { id: "",
            name: '',
            email: '',
            entries: 0,
            joined: '',
            rollData: '',

        }
		}

      this.OnRoll = this.OnRoll.bind(this);
      console.log(this.state)

	}

  loadUser = (data) => {
    this.setState( {user: { id: "",
            name: data.name,
            email: data.email,
            entries: data.entries,
            joined: data.joined,
            rollData: data.rolldata,
          }})
  }



    // the data from the randomly generated box is passed here and set to state. 
    OnRoll = (child, number) => {
        this.setState({initialstate: false})
        const result = child;
        const num = number;
        this.setState({task: result})
        this.setState({number: num })
    }
  // resonsible in part for decideding what components are shown.
  onRouteChange =(route) =>{
      console.log(route)
       if (route === 'home'){
  		this.setState({route: 'home'})

    }
      else if (route === "rollAgain"){
        this.setState({initialstate: true})
      }
    }





  render() {
   const { route, initialstate} = this.state;
  	return( 
  		<div className='App'>


    <Navigation onRouteChange={this.onRouteChange} initialstate={this.state.initialstate}/>
     {route === 'home'?
     <div>

      {initialstate ? (
         <div>
         <Header/>
         <TextBox onRoll={this.OnRoll}/> 
         </div>
         ) :(
           <ResultBox result={this.state.task} number={this.state.number}/>
          )}
       </div>

     :(route === 'signin'
           ?<SignIn  onRouteChange={this.onRouteChange} loadUser={this.loadUser}/>
           :<Register onRouteChange={this.onRouteChange} loadUser={this.loadUser} /> 

     )
   }
     </div>

    );         
}
}


export default App;
