import React from 'react';


class SignIn extends React.Component {
	constructor(props) {
	super(props);
	this.state = {
		signInEmail: '',
		signInPassword: '',
	  }
	}


	onEmailChange=(event)=> {
		this.setState({signInEmail: event.target.value})
	}
    onPasswordChange=(event)=> {
		this.setState({signInPassword: event.target.value})
	}

	saveAuthTokenInSession =(token) => {
		window.sessionStorage.setItem('token', token);

	}

	onSubmitSignin = () => {
		fetch('http://localhost:3000/signin', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.signInEmail,
				password: this.state.signInPassword,
			})
		}) 
		.then(response => response.json())
		.then(data=> { 
			console.log(data)
			if (data.userId && data.success === 'true') {
		      this.saveAuthTokenInSession(data.token)
			  fetch(`http://localhost:3000/signinPf/${data.userId}`,{
              method : 'get',
              headers: {
              'Content-Type': 'application/json',
              'Authorization': data.token
            }
            })
            .then(resp => resp.json())
            .then( user =>{
              if (user && user.email){
                this.props.loadUser(user)
                this.props.onRouteChange('home');
              }
          })
        .catch(console.log)
		}
	})
}

	render() {

	  return(
		<article className=" color cont br3 ba b--black-10 mv4 w-100 w-50-m w-25-l mw5 shadow-5 center">
			<main className="pa4 black-80">
			  <div className="measure">
			    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
			      <legend className="f3 fw6 ph0 white mh0">Sign In</legend>
			      <div className="mt3">
			        <label className="db white fw6 lh-copy f6" htmlFor="email-address">Email</label>
			        <input
			         onChange= {this.onEmailChange}
			         className="pa2  input-reset ba  w-100" 
			         type="email" 
			         name="email-address"  
			         id="email-address"/>
			      </div>
				      <div className="mv3">
				        <label className="db white fw6 lh-copy f6" 
				        htmlFor="password">Password</label>
				        <input
				        onChange={this.onPasswordChange}
				         className="b pa2 input-reset ba   w-100"
				         type="password"
				         name="password" 
				          id="password"/>
				      </div>
				    </fieldset>
					    <div className="">
					      <input 
					      onClick= {this.onSubmitSignin}
					      className="b white  ph3 pv2 input-reset white ba b--white bg-transparent grow pointer f6 dib" 
					      type="submit"
					      value="Sign in"/>
					   </div>
				    <div className="lh-copy mt3">
			      <p onClick={this.onSubmitSignin} 
			      className="f6 link dim white db white pointer ">Register</p>
			    </div>
			  </div>
			</main>
		</article>

		);
	}
}

export default SignIn;
