import React, { Component } from 'react';

import './App.css';


import Register from './components/Register/Register';
import SignIn from './components/SignIn/SignIn';
import TextBox from './components/TextBox/TextBox';
import ResultBox from './components/TextBox/ResultBox';
import LoginNavigation from './components/Navigation/LoginNavigation';
import Header from './components/Header/Header';
import UserPage from './components/UserPage/UserPage';



class App extends Component {
	constructor(){
		super()
			this.state= {
				initialstate: true,
				task: '',
				number: '',
        route: 'signin',
        isSignedIn: "false",
        user: { id: "",
            name: '',
            email: '',
            entries: 0,
            joined: '',
            rollData: '',

        }
		}

      this.OnRoll = this.OnRoll.bind(this);
      console.log(this.state)

	}

fetchprofile = ({id}) => {
    fetch(`http://localhost:3000/profile/${id}`, {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        email: this.state.email,
        password: this.state.password,
        name: this.state.name,
      })
    }) 
    .then(response => response.json())
    .then(user => {
      if (user.id) {
         console.log(user)
      }
    })
  }




  loadUser = (data) => {

    this.setState( {user: { id: "",
            name: data.name,
            email: data.email,
            entries: data.entries,
            joined: data.joined,
            rollData: data.rollData,
          }})
  }





    // the data from the randomly generated box is passed here and set to state. 
    OnRoll = (child, number) => {
        this.setState({initialstate: false})
        const result = child;
        const num = number;
        this.setState({task: result})
        this.setState({number: num })
    }


  onRouteChange = (route) => {
    if (route === 'signout') {
      this.setState({route: "signin"})
      this.setState({isSignedIn: "false"})
    } else if ( route === 'home') {
      this.setState({isSignedIn: "true"})
    } else if (route === "userPage"){
      this.setState({isSignedIn: "user"})
      this.setState({initialstate: true})
    }
    this.setState({route: route});
  }

  onRollChange =(rollRoute) =>{
    if (rollRoute === "rollAgain"){
      this.setState({initialstate: true})
    } else {
      this.setState({initialstate: false})
    }
  }

  render() {
   const { route, initialstate} = this.state;
  	return( 
  		<div className='App'>

    <LoginNavigation onRollChange={this.onRollChange} isSignedIn={this.state.isSignedIn} onRouteChange={this.onRouteChange} initialstate={this.state.initialstate}/>
     {route === 'home'?
     <div>
      {initialstate ? (
         <div>
         <Header/>
         <TextBox onRoll={this.OnRoll}/> 
         </div>
         ) :(
           <ResultBox onRollChange={this.onRollChange} result={this.state.task} number={this.state.number}/>
          )}
      </div>
      : route === 'userPage'?

      <div>
       <UserPage onRollChange={this.onRollChange} user={this.state.user} />
      </div>

     :(route === 'signin'
           ?<SignIn  onRouteChange={this.onRouteChange} loadUser={this.loadUser}/>
           :<Register onRouteChange={this.onRouteChange} loadUser={this.loadUser} /> 

     )
   }
     </div>

    );         
}
}


export default App;
