import React, { Component } from 'react';

import './App.css';


import Register from './components/Register/Register';
import SignIn from './components/SignIn/SignIn';
import TextBox from './components/TextBox/TextBox';
import ResultBox from './components/TextBox/ResultBox';
import Navigation from './components/Navigation/Navigation';
import Header from './components/Header/Header';
// import Error from './components/Error/Error';




class App extends Component {
	constructor(){
		super()
			this.state= {
				rollState: true,
				task: '',
				number: '',
        route: true,
		}

      this.OnRoll = this.OnRoll.bind(this);

	}

    // the data from the randomly generated box is passed here and set to state. 
    OnRoll = (child, number) => {
        this.setState({rollState: false})
        const result = child;
        const num = number;
        this.setState({task: result})
        this.setState({number: num })
    }
  // resonsible in part for decideding what components are shown.
  onRouteChange = (route)=>{
  	if ( route === 'home'){
  		this.setState({route: false})
  	}
  }

  render() {

  	return( 
  		<div>


    <Navigation onRouteChange={this.onRouteChange} rollState={this.state.rollState}/>
    {this.state.route === false ?

          <SignIn onRouteChange={this.onRouteChange} />
           : 
           <div>
            <Register/> 
  	{this.state.route === false ?

      <div>
        <Header/>
         <TextBox onRoll={this.OnRoll}/> 
      </div>

       :<div>
       <ResultBox result={this.state.task} number={this.state.number}/>
       </div>
     } 

     </div>}
  </div>);         
}
}


export default App;
