import React from 'react';
import './Register.css'


class Register extends React.Component {
   	constructor(props) {
	super(props);
	this.state = {
		email: '',
		password: '',
		name: '',
	  }
	}

	onEmailChange=(event)=> {
		this.setState({email: event.target.value})
	}
    onPasswordChange=(event)=> {
		this.setState({password: event.target.value})
	}

	onNameChange=(event)=> {
		this.setState({name: event.target.value})
	}
		saveAuthTokenInSession =(token) => {
		window.sessionStorage.setItem('token', token);

	}

	onSubmitSignin = () => {
		fetch('http://localhost:3000/register', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.email,
				password: this.state.password,
				name: this.state.name,
			})
		}) 
		.then(data=> { 
			if (data) {
		      this.saveAuthTokenInSession(data.token)
			  fetch(`http://localhost:3000/signinPf/${data.Id}`,{
              method : 'get',
              headers: {
              'Content-Type': 'application/json',
              'Authorization': data.token
            }
            })
            .then(resp => resp.json())
            .then( user =>{
              if (user && user.email){
                this.props.loadUser(user)
                this.props.onRouteChange('home');
              }
          })
        .catch(console.log)
		}
	})
}


   render() {
		return(
			<article className=" border br2  white mv4 w-100 w-50-m w-25-l mw5 shadow-5 center">
			<main className="pa4 black">
			  <div className="measure">
			    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
			      <legend className="f3.5 subh b fw6 ph0 mh0">Register</legend>
			      <div className="mt3">
			        <label className="db fw6 subh lh-copy f6" htmlFor="name">Name</label>
			        <input 
			        onChange={this.onNameChange}
			        className="pa2 black input-reset subh ba bg-transparent hover-bg-black  w-100" 
			        type="text" 
			        name="name"  
			        id="name"/>
			      </div>
			      <div className="mt3">
			        <label className="db subh fw6 lh-copy f6" htmlFor="email-address">Email</label>
			        <input 
			        onChange={this.onEmailChange}
			        className="pa2 subh input-reset ba bg-transparent hover-bg-black  w-100"
			         type="email" 
			         name="email-address" 
			          id="email-address"/>
			      </div>
			      <div className="mv3">
			        <label className="db fw6  subh lh-copy f6" htmlFor="password">Password</label>
			        <input 
			        onChange={this.onPasswordChange}
			        className="b pa2  subh input-reset ba bg-transparent hover-bg-black  w-100" 
			        type="password" 
			        name="password"  
			        id="password"/>
			      </div>
			    </fieldset>
			    <div className="">
			      <input 
			      onClick={this.onSubmitSignin} 
			      className="b subh ph3 pv2 subh input-reset ba b--black bg-transparent grow pointer f6 dib" 
			      type="submit" value="Register"/>
			    </div>
			  </div>
			</main>
			</article>
	 );
}
}

export default Register;
