if (data.roll_id){
  return true;
} else if (data.roll_id === null) { 
  return false
}
}



class Profile extends React.Component {
  constructor(props){
    super(props)
    this.state = { 
      id: this.props.user.id,
      name: this.props.user.name,
      rollData: []   
    }
  }

  componentDidMount(){ 

    fetch(`http://localhost:3000/profile/${this.state.id}`,{
      method: 'get',
      headers: {'Content-Type': 'application/json',
          'Authorization': window.sessionStorage.getItem('token') 
           },
    }).then(user => {
      if (user.id) {
        this.b(false)

      }
    })
  }



render() {
  const dat = this.state.rollData;
  const data = dat.reverse();
  console.log(data[0])





     // const data = List;

   return ( 

      	<div className="profile-modal">
      	 <article className=" insideCoponent shadow-3 mv4 tc pa4  mw7 mw7-ns center    ">
      	   <Header name={this.state.name} />  

            <Scroll>         
       {

            b(data) ? (

        data.map((user, i) => {
        if (data[i].rolldata){
        return (  
          <Posts key={i} 
          id={data[i].roll_id}
          data={data[i].rolldata} 
          date={data[i].rolldate}
                  /> 
            )} 
          }
        )) : (
             <div className="black">
             hello
             </div>
              )



      }

             </Scroll>     
           <button className="mt3 ml1s " onClick={this.props.toggleModal}>Return</button>
        </article>
      </div> 
  	)
  }
}



export default Profile;
