import React from 'react';
import './Profile.css'

import listsOfPosts from './listsOfPosts';
import Header from './Header';
import Scroll from './Scroll';
import Posts from './Posts';
// import Cardlist from './Cardlist'

// isProfileOpen, toggleModal
class Profile extends React.Component {
  constructor(props){
    super(props)
    this.state = { 
      id: this.props.user.id,
      name: this.props.user.name,
      rollData: []

    }



  }


  onPostFetch = () => {
    fetch(`http://localhost:3000/profile/${this.state.user.id}`,{
      method: 'get',
      headers: {'Content-Type': 'application/json'},
    }).then(resp => resp.json())
      .then( user => this.setState({rollData: user}));
  }


  render() {
const data = this.state.rollData;

  return (

  	<div 
  	className="profile-modal">


  	<article 
    className=" insideCoponent shadow-3 ph4 tc w-100 mw5.5 mw6-ns center pa3 mb4  ">
  	    <Header name={this.state.name} />     
        <Scroll>
    {
       data.map((user, i) => {
        if (data[i].rolldata){
        return (  
          <Posts key={i} 
          id={data[i].roll_id}
          data={data[i].rolldata} 
          date={data[i].rolldate}
            />
        );
      }})
    }
     </Scroll>
     <button onClick={this.props.toggleModal}>Return</button>
  </article>
    </div> 
  	)
}
}

export default Profile;
