import React, { Component } from 'react';

import './App.css';


import Register from './components/Register/Register';
import SignIn from './components/SignIn/SignIn';
import TextBox from './components/TextBox/TextBox';
import ResultBox from './components/TextBox/ResultBox';
import LoginNavigation from './components/Navigation/LoginNavigation';
import Header from './components/Header/Header';
import Modal from './components/Modal/Modal';
import Profile from './components/profile/Profile';



class App extends Component {
	constructor(){
		super()
			this.state= {
				initialstate: true,
				task: '',
				number: '',
        route: 'home',
        // signin ^
        isSignedIn: "true",
        // false
        isProfileOpen: false,
        user: { id: "",
            name: '',
            email: '',
            entries: 0,
            joined: '',
            rollData: '',

        }
		}

      this.OnRoll = this.OnRoll.bind(this);
      console.log(this.state)

	}

  loadUser = (data) => {
    this.setState( {user: { id: "",
            name: data.name,
            email: data.email,
            entries: data.entries,
            joined: data.joined,
            rollData: data.rollData,
          }})
  }



    // the data from the randomly generated box is passed here and set to state. 
    OnRoll = (child, number) => {
        this.setState({initialstate: false})
        const result = child;
        const num = number;
        this.setState({task: result})
        this.setState({number: num })
    }


  onRouteChange = (route) => {
    if (route === 'signout') {
      this.setState({route: "signin"})
      this.setState({isSignedIn: "false"})
    } else if ( route === 'home') {
      this.setState({isSignedIn: "true"})
    }
    this.setState({route: route});
  }

  // onRollChange =(rollRoute) =>{
  //   if (rollRoute === "rollAgain"){
  //     this.setState({initialstate: true})
  //   } else {
  //     this.setState({initialstate: false})
  //   }
  // }

  toggleModal = () => {
    this.setState(prevState => ({
      ...prevState,
      isProfileOpen: !prevState.isProfileOpen
    }))
  }

  render() {
   const { route, initialstate, isProfileOpen} = this.state;
  	return( 
  		<div className='App'>
      <LoginNavigation 
     toggleModal={this.toggleModal}
      onRollChange={this.onRollChange}
      isSignedIn={this.state.isSignedIn}
      onRouteChange={this.onRouteChange} 
      initialstate={this.state.initialstate}
    />
        {isProfileOpen && 
          <Modal >
            <Profile 
            isProfileOpen={isProfileOpen} 
            toggleModal={this.toggleModal} 
            onRouteChange={this.onRouteChange} 
            />
         </Modal>}


     {route === 'home'?

     <div>
      {initialstate ? (
        <div>
          <Header/>
          <TextBox onRoll={this.OnRoll}/> 
        </div>

        ) 
      :(
          <ResultBox onRollChange={this.onRollChange} result={this.state.task} number={this.state.number}/>
        )}

      </div>

     :(route === 'signin'
           ?<SignIn  onRouteChange={this.onRouteChange} loadUser={this.loadUser}/>
           :<Register onRouteChange={this.onRouteChange} loadUser={this.loadUser} /> 

     )
   }
     </div>

    );         
}
}


export default App;
